import React, { useEffect, useState } from 'react'
import '../../node_modules/bootstrap/dist/css/bootstrap.min.css'
import Navbar from './Navbar'
import {addMovies} from '../actions/index'
import data from '../data'
import MovieCard from './MovieCard'

export default function App(props){
    const [movies,setMovies] = useState({
        list:[],
        favorites:[]
    });


    useEffect(()=>{

        props.store.dispatch(addMovies(data))

    },[])

    props.store.subscribe(()=>{
            setMovies(props.store.getState())
            console.log("Updated")
            console.log(props.store.getState())
        })

       const isMovieFavourite = (movie) =>{
        const {favoutites} = props.store.getState();
        const index = favoutites.indexOf(movie)
        if(index !== -1){
            console.log("Found Movie")
            return true;
        }
        return false;



       }

    return (

    <>
    <div className="container">
        <Navbar/>
           <div className="container mt-5">
            <div className="row tabs">
                <div className="tab col-md-5">Movies</div>
                <div className="tab col-md-5" >Favorites</div>
            </div>

            <div className="list">
            <div className="row mt-5">
            {

                movies.list.map((movie, index)=>{
                    return <MovieCard movie={movie} key={index} dispatch={props.store.dispatch} />
                })

            }
            </div>

            </div>

           </div>

        </div>

        </>

    )
}
